package edu.usc.cs.group8.ImHungry;

/*
 * Result.java
 * Superclass of Restaurant and Result, to be used by the List Management system.
 * Author: Kevin Calaway
 * USC ID: 9724507315
 * Email: calaway@usc.edu
 */
public abstract class Result {
	
	/*
	 * The only field recipes share in common with results is a name.
	 */
	private String name;
	
	public String getName() {
		return name;
	}
	
	public void setName(String name) {
		this.name = name;
	}

	/*
	 * Autogenerated equals and hashCode methods for use in comparators
	 * and identifiers.
	 */

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		Result other = (Result) obj;
		if (name == null) {
			if (other.name != null)
				return false;
		} else if (!name.equals(other.name))
			return false;
		return true;
	}

	
}
